import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import type { RootState } from '../store';
import { useWebSocket } from './useWebSocket';
import { setError } from '../store/slices/authSlice';

export const useAuth = (): {
  isAuthenticated: boolean;
  accessToken: string | null;
  loading: boolean;
  error: string | null;
} => {
  const dispatch = useDispatch();
  const { isAuthenticated, accessToken, loading, error } = useSelector(
    (state: RootState) => state.auth
  );
  const { connect, disconnect } = useWebSocket();

  useEffect(() => {
    if (isAuthenticated && accessToken) {
      // When authenticated, connect to WebSocket
      connect();
    } else {
      // When not authenticated, disconnect from WebSocket
      disconnect();
    }
  }, [isAuthenticated, accessToken, connect, disconnect]);

  useEffect(() => {
    // Check if token exists in localStorage but not in state
    const storedToken = localStorage.getItem('kite_access_token');
    if (storedToken && !accessToken) {
      dispatch(setError('Session expired. Please login again.'));
    }
  }, [dispatch, accessToken]);

  return {
    isAuthenticated,
    accessToken,
    loading,
    error,
  };
};
