import axios from 'axios';
import { store } from '../store';
import { MarketData, OptionChainData } from '../types/market';

const API_URL = 'http://localhost:8000/api/v1';

// Create axios instance with base configuration
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add request interceptor to include access token
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('kite_access_token');
  if (token) {
    config.headers['Authorization'] = `Bearer ${token}`;
  }
  return config;
});

// Add response interceptor to handle errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Handle unauthorized access
      localStorage.removeItem('kite_access_token');
    }
    return Promise.reject(error);
  }
);

// API endpoints
export const kiteApi = {
  getLoginUrl: async () => {
    const response = await api.get('/kite/login');
    return response.data.login_url;
  },

  handleCallback: async (requestToken: string) => {
    const response = await api.get(`/kite/callback?request_token=${requestToken}`);
    return response.data.access_token;
  },

  setToken: (token: string) => api.post('/kite/token', { access_token: token }),
  validateToken: (token: string) => api.get(`/kite/verify-token/${token}`),
  getInstruments: () => api.get('/kite/instruments'),
  getOptionChain: (symbol: string, expiry?: string): Promise<OptionChainData> => 
    api.get(`/kite/options/chain/${symbol}`, { params: { expiry } }),
  getProfile: () => api.get('/kite/profile'),
};

export default api;
