import React, { useState, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Box, TextField, Button, CircularProgress, Snackbar, Alert } from '@mui/material';
import type { RootState } from '../../store';
import { setAccessToken, setError as setAuthError } from '../../store/slices/authSlice';
import { useWebSocket } from '../../hooks/useWebSocket';
import axios from 'axios';

const BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000/api/v1';

export const AccessTokenInput: React.FC = () => {
  const dispatch = useDispatch();
  const { isAuthenticated, loading: authLoading, error: authError } = useSelector((state: RootState) => state.auth);
  const [token, setToken] = useState('');
  const [loading, setLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const { connect } = useWebSocket();

  const validateAndSetToken = async (tokenToValidate: string) => {
    setLoading(true);
    try {
      // First set the token using form data
      const formData = new FormData();
      formData.append('access_token', tokenToValidate);

      await axios.post(`${BASE_URL}/kite/token`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        }
      });

      // Then validate it
      const validateResponse = await axios.get(`${BASE_URL}/kite/validate`, {
        headers: {
          'Authorization': `Bearer ${tokenToValidate}`
        }
      });

      if (validateResponse.status !== 200) {
        throw new Error('Token validation failed');
      }

      // If validation successful, store token and update state
      localStorage.setItem('kite_access_token', tokenToValidate);
      dispatch(setAccessToken(tokenToValidate));

      // Initialize WebSocket connection after token is set
      connect();

      setSuccessMessage('Access token validated successfully');
      if (tokenToValidate === token) {
        setToken('');
      }
    } catch (error: any) {
      console.error('Error validating access token:', error);
      const errorMessage = error.response?.data?.detail || error.message || 'Invalid access token';
      dispatch(setAuthError(errorMessage));
      dispatch(setAccessToken(null));
      localStorage.removeItem('kite_access_token');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = useCallback(async (e: React.FormEvent) => {
    e.preventDefault();
    if (!token.trim()) {
      dispatch(setAuthError('Please enter an access token'));
      return;
    }

    await validateAndSetToken(token);
  }, [token, dispatch]);

  const handleTokenChange = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    setToken(e.target.value);
    if (authError) {
      dispatch(setAuthError(null));
    }
  }, [authError, dispatch]);

  const handleClose = () => {
    setSuccessMessage('');
  };

  return (
    <Box
      component="form"
      onSubmit={handleSubmit}
      sx={{
        display: 'flex',
        flexDirection: 'column',
        gap: 2,
        maxWidth: 400,
        margin: '0 auto',
        padding: 2,
      }}
    >
      <TextField
        fullWidth
        label="Access Token"
        value={token}
        onChange={handleTokenChange}
        error={Boolean(authError)}
        helperText={authError || ''}
        disabled={loading || authLoading}
      />
      <Button
        type="submit"
        variant="contained"
        disabled={loading || authLoading}
        sx={{ position: 'relative' }}
      >
        {loading || authLoading ? (
          <CircularProgress size={24} sx={{ position: 'absolute' }} />
        ) : isAuthenticated ? 'Update Token' : 'Set Token'}
      </Button>

      <Snackbar
        open={Boolean(successMessage)}
        autoHideDuration={6000}
        onClose={handleClose}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={handleClose} severity="success" sx={{ width: '100%' }}>
          {successMessage}
        </Alert>
      </Snackbar>
    </Box>
  );
};
