import axios from 'axios';
import { MarketData, OptionChainData } from '../types/market';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000/api/v1';

const api = axios.create({
    baseURL: API_URL,
    headers: {
        'Content-Type': 'application/json',
    },
});

export const kiteApi = {
    getLoginUrl: async () => {
        const response = await api.get('/kite/login');
        return response.data.login_url;
    },

    handleCallback: async (requestToken: string) => {
        const response = await api.get(`/kite/callback?request_token=${requestToken}`);
        return response.data.access_token;
    },

    validateToken: async () => {
        const response = await api.get('/kite/validate');
        return response.data.status === 'valid';
    },

    getInstruments: async () => {
        const response = await api.get('/kite/instruments');
        return response.data;
    },

    getOptionChain: async (symbol: string, expiry?: string): Promise<OptionChainData> => {
        const params = expiry ? { expiry } : {};
        const response = await api.get(`/kite/option-chain/${symbol}`, { params });
        return response.data;
    },
};
