import { createSlice, PayloadAction } from '@reduxjs/toolkit';

export interface AuthState {
    accessToken: string | null;
    isAuthenticated: boolean;
    loading: boolean;
    error: string | null;
}

const initialState: AuthState = {
    accessToken: localStorage.getItem('kite_access_token'),
    isAuthenticated: Boolean(localStorage.getItem('kite_access_token')),
    loading: false,
    error: null,
};

export const authSlice = createSlice({
    name: 'auth',
    initialState,
    reducers: {
        setAccessToken: (state, action: PayloadAction<string>) => {
            state.accessToken = action.payload;
            state.isAuthenticated = true;
            state.error = null;
            localStorage.setItem('kite_access_token', action.payload);
        },
        setLoading: (state, action: PayloadAction<boolean>) => {
            state.loading = action.payload;
        },
        setError: (state, action: PayloadAction<string>) => {
            state.error = action.payload;
            state.loading = false;
            state.isAuthenticated = false;
            localStorage.removeItem('kite_access_token');
        },
        logout: (state) => {
            state.accessToken = null;
            state.isAuthenticated = false;
            state.error = null;
            localStorage.removeItem('kite_access_token');
        },
        clearError: (state) => {
            state.error = null;
        },
    },
});

export const {
    setAccessToken,
    setLoading,
    setError,
    logout,
    clearError,
} = authSlice.actions;

export default authSlice.reducer;
