import { useEffect, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import type { RootState } from '../store';
import { websocketService } from '../services/websocket';
import {
    setConnected,
    setClientId,
    addSubscription,
    removeSubscription,
    setError,
} from '../store/slices/websocketSlice';

export const useWebSocket = () => {
    const dispatch = useDispatch();
    const { connected, clientId, subscriptions, error } = useSelector(
        (state: RootState) => state.websocket
    );

    const connect = useCallback(() => {
        if (!connected) {
            const newClientId = clientId || `client-${Date.now()}`;
            websocketService.connect(newClientId, subscriptions);
            dispatch(setClientId(newClientId));
            dispatch(setConnected(true));
        }
    }, [dispatch, connected, clientId, subscriptions]);

    const disconnect = useCallback(() => {
        websocketService.disconnect();
        dispatch(setConnected(false));
    }, [dispatch]);

    const subscribe = useCallback((instrumentToken: number) => {
        if (connected) {
            websocketService.subscribe([instrumentToken]);
            dispatch(addSubscription(instrumentToken));
        }
    }, [dispatch, connected]);

    const unsubscribe = useCallback((instrumentToken: number) => {
        if (connected) {
            websocketService.unsubscribe([instrumentToken]);
            dispatch(removeSubscription(instrumentToken));
        }
    }, [dispatch, connected]);

    useEffect(() => {
        connect();
        return () => {
            disconnect();
        };
    }, [connect, disconnect]);

    return {
        connected,
        clientId,
        subscriptions,
        error,
        connect,
        disconnect,
        subscribe,
        unsubscribe,
    };
};
