import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Button,
  Typography,
  SelectChangeEvent,
  CircularProgress,
} from '@mui/material';
import { useOptionChainData } from './hooks/useOptionChainData';
import { useMarketData } from '../../hooks/useMarketData';

interface Props {
  symbol: string;
}

export const OptionChainGrid: React.FC<Props> = ({ symbol: initialSymbol }) => {
  const [selectedSymbol, setSelectedSymbol] = useState(initialSymbol);
  const [selectedExpiry, setSelectedExpiry] = useState<string>('');
  const [threshold, setThreshold] = useState('0.05');

  // Get expiry dates and option chain data
  const {
    expiryDates,
    loading: expiryLoading,
    error: expiryError,
    fetchExpiryDates,
  } = useOptionChainData({
    symbol: selectedSymbol,
    expiry: selectedExpiry,
    threshold: parseFloat(threshold)
  });

  // Get market data
  const { optionChainData } = useMarketData(selectedSymbol, selectedExpiry);

  // Fetch expiry dates when symbol changes
  useEffect(() => {
    fetchExpiryDates(selectedSymbol);
  }, [selectedSymbol, fetchExpiryDates]);

  const handleSymbolChange = (event: SelectChangeEvent<string>) => {
    const newSymbol = event.target.value;
    setSelectedSymbol(newSymbol);
    setSelectedExpiry(''); // Reset expiry when symbol changes
  };

  const handleExpiryChange = (event: SelectChangeEvent<string>) => {
    setSelectedExpiry(event.target.value);
  };

  const handleThresholdChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setThreshold(event.target.value);
  };

  return (
    <Box>
      <Paper elevation={3} sx={{ p: 2, mb: 2 }}>
        <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
          <FormControl sx={{ minWidth: 120 }}>
            <InputLabel>Symbol</InputLabel>
            <Select
              value={selectedSymbol}
              label="Symbol"
              onChange={handleSymbolChange}
              disabled={expiryLoading}
            >
              <MenuItem value="NIFTY">NIFTY</MenuItem>
              <MenuItem value="BANKNIFTY">BANKNIFTY</MenuItem>
            </Select>
          </FormControl>

          <FormControl sx={{ minWidth: 120 }}>
            <InputLabel>Expiry</InputLabel>
            <Select
              value={selectedExpiry}
              label="Expiry"
              onChange={handleExpiryChange}
              disabled={expiryLoading || expiryDates.length === 0}
            >
              {expiryDates.map(date => (
                <MenuItem key={date} value={date}>{date}</MenuItem>
              ))}
            </Select>
          </FormControl>

          <TextField
            label="Threshold"
            value={threshold}
            onChange={handleThresholdChange}
            sx={{ width: 100 }}
            disabled={expiryLoading}
          />
        </Box>
      </Paper>

      {expiryError && (
        <Typography color="error" sx={{ mt: 2 }}>
          {expiryError}
        </Typography>
      )}

      {/* Stock Information */}
      {optionChainData?.stockInfo && (
        <Paper elevation={3} sx={{ p: 2, mb: 2 }}>
          <Box sx={{ display: 'flex', gap: 4 }}>
            <Typography>
              <strong>Stock:</strong> {optionChainData.stockInfo.name}
            </Typography>
            <Typography>
              <strong>LTP:</strong> {optionChainData.stockInfo.ltp}
            </Typography>
            <Typography>
              <strong>Change:</strong> {optionChainData.stockInfo.change}
            </Typography>
            <Typography>
              <strong>Lot Size:</strong> {optionChainData.stockInfo.lotSize}
            </Typography>
            <Typography>
              <strong>Future:</strong> {optionChainData.stockInfo.future}
            </Typography>
            <Typography>
              <strong>VIX:</strong> {optionChainData.stockInfo.vix}
            </Typography>
          </Box>
        </Paper>
      )}

      {/* Option Chain Grid */}
      {optionChainData?.strikes && (
        <Paper elevation={3} sx={{ p: 2, height: 'calc(100vh - 300px)' }}>
          <Box sx={{ display: 'flex', gap: 2, height: '100%' }}>
            {/* Calls Grid */}
            <Box sx={{ flex: 1 }}>
              <Typography variant="h6" align="center" sx={{ mb: 2 }}>
                Calls
              </Typography>
              <Box sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                <Box sx={{ 
                  display: 'grid', 
                  gridTemplateColumns: 'repeat(5, 1fr)',
                  gap: 1,
                  p: 1,
                  bgcolor: 'primary.main',
                  color: 'white',
                }}>
                  <Typography>LTP</Typography>
                  <Typography>Change%</Typography>
                  <Typography>Volume</Typography>
                  <Typography>OI</Typography>
                  <Typography>IV</Typography>
                </Box>
                <Box sx={{ flex: 1, overflow: 'auto' }}>
                  {optionChainData.strikes.map((strike) => (
                    <Box 
                      key={`call-${strike.strike_price}`}
                      sx={{ 
                        display: 'grid', 
                        gridTemplateColumns: 'repeat(5, 1fr)',
                        gap: 1,
                        p: 1,
                        '&:nth-of-type(odd)': { bgcolor: 'grey.100' },
                        '&:hover': { bgcolor: 'action.hover' }
                      }}
                    >
                      <Typography>{strike.call.ltp?.toFixed(2) || '-'}</Typography>
                      <Typography 
                        sx={{ 
                          color: strike.call.change >= 0 ? 'success.main' : 'error.main'
                        }}
                      >
                        {strike.call.change?.toFixed(2) || '-'}%
                      </Typography>
                      <Typography>{strike.call.volume || '-'}</Typography>
                      <Typography>{strike.call.oi || '-'}</Typography>
                      <Typography>{strike.call.iv?.toFixed(2) || '-'}</Typography>
                    </Box>
                  ))}
                </Box>
              </Box>
            </Box>

            {/* Strike Prices */}
            <Box sx={{ width: 120 }}>
              <Typography variant="h6" align="center" sx={{ mb: 2 }}>
                Strike
              </Typography>
              <Box sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                <Box sx={{ 
                  p: 1,
                  bgcolor: 'primary.main',
                  color: 'white',
                  textAlign: 'center'
                }}>
                  <Typography>Price</Typography>
                </Box>
                <Box sx={{ flex: 1, overflow: 'auto' }}>
                  {optionChainData.strikes.map((strike) => (
                    <Box 
                      key={`strike-${strike.strike_price}`}
                      sx={{ 
                        p: 1,
                        textAlign: 'center',
                        fontWeight: 'bold',
                        '&:nth-of-type(odd)': { bgcolor: 'grey.100' },
                        '&:hover': { bgcolor: 'action.hover' }
                      }}
                    >
                      <Typography>{strike.strike_price}</Typography>
                    </Box>
                  ))}
                </Box>
              </Box>
            </Box>

            {/* Puts Grid */}
            <Box sx={{ flex: 1 }}>
              <Typography variant="h6" align="center" sx={{ mb: 2 }}>
                Puts
              </Typography>
              <Box sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                <Box sx={{ 
                  display: 'grid', 
                  gridTemplateColumns: 'repeat(5, 1fr)',
                  gap: 1,
                  p: 1,
                  bgcolor: 'primary.main',
                  color: 'white',
                }}>
                  <Typography>LTP</Typography>
                  <Typography>Change%</Typography>
                  <Typography>Volume</Typography>
                  <Typography>OI</Typography>
                  <Typography>IV</Typography>
                </Box>
                <Box sx={{ flex: 1, overflow: 'auto' }}>
                  {optionChainData.strikes.map((strike) => (
                    <Box 
                      key={`put-${strike.strike_price}`}
                      sx={{ 
                        display: 'grid', 
                        gridTemplateColumns: 'repeat(5, 1fr)',
                        gap: 1,
                        p: 1,
                        '&:nth-of-type(odd)': { bgcolor: 'grey.100' },
                        '&:hover': { bgcolor: 'action.hover' }
                      }}
                    >
                      <Typography>{strike.put.ltp?.toFixed(2) || '-'}</Typography>
                      <Typography 
                        sx={{ 
                          color: strike.put.change >= 0 ? 'success.main' : 'error.main'
                        }}
                      >
                        {strike.put.change?.toFixed(2) || '-'}%
                      </Typography>
                      <Typography>{strike.put.volume || '-'}</Typography>
                      <Typography>{strike.put.oi || '-'}</Typography>
                      <Typography>{strike.put.iv?.toFixed(2) || '-'}</Typography>
                    </Box>
                  ))}
                </Box>
              </Box>
            </Box>
          </Box>
        </Paper>
      )}
    </Box>
  );
};
