import React, { useState, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Box, TextField, Button, CircularProgress, Snackbar, Alert } from '@mui/material';
import type { RootState } from '../../store';
import { setAccessToken } from '../../store/slices/authSlice';
import { useWebSocket } from '../../hooks/useWebSocket';

export const AccessTokenInput: React.FC = () => {
  const dispatch = useDispatch();
  const { isAuthenticated } = useSelector((state: RootState) => state.auth);
  const [token, setToken] = useState('');
  const [loading, setLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const [error, setError] = useState<string | null>(null);
  const { connect } = useWebSocket();

  const handleSubmit = useCallback(async (e: React.FormEvent) => {
    e.preventDefault();
    if (!token.trim()) {
      setError('Please enter an access token');
      return;
    }

    setLoading(true);
    try {
      // Store the token
      localStorage.setItem('kite_access_token', token);

      // Update Redux state
      dispatch(setAccessToken(token));

      // Initialize WebSocket connection after token is set
      setTimeout(() => {
        connect();
        setSuccessMessage('Access token saved successfully');
        setToken('');
        setLoading(false);
      }, 0);
    } catch (err) {
      console.error('Error setting access token:', err);
      setError('Error setting access token');
      setLoading(false);
    }
  }, [token, dispatch, connect]);

  const handleTokenChange = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    setToken(e.target.value);
  }, []);

  const handleClose = () => {
    setSuccessMessage('');
  };

  const handleErrorClose = () => {
    setError(null);
  };

  return (
    <Box
      component="form"
      onSubmit={handleSubmit}
      sx={{
        display: 'flex',
        flexDirection: 'column',
        gap: 2,
        width: '100%',
      }}
    >
      <TextField
        fullWidth
        label="Access Token"
        variant="outlined"
        value={token}
        onChange={handleTokenChange}
        disabled={loading || isAuthenticated}
        placeholder="Enter your Kite access token"
      />
      <Button
        type="submit"
        variant="contained"
        color="primary"
        disabled={loading || !token.trim() || isAuthenticated}
        sx={{ alignSelf: 'flex-start' }}
      >
        {loading ? <CircularProgress size={24} /> : 'Save Token'}
      </Button>

      <Snackbar
        open={!!successMessage}
        autoHideDuration={6000}
        onClose={handleClose}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={handleClose} severity="success">
          {successMessage}
        </Alert>
      </Snackbar>

      <Snackbar
        open={!!error}
        autoHideDuration={6000}
        onClose={handleErrorClose}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={handleErrorClose} severity="error">
          {error}
        </Alert>
      </Snackbar>
    </Box>
  );
};
