import {
  Box,
  Paper,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Typography,
  SelectChangeEvent,
  CircularProgress,
  Button,
  Divider,
  Card,
  CardContent,
  Chip,
  IconButton,
  Tooltip,
} from '@mui/material';
import RefreshIcon from '@mui/icons-material/Refresh';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import TrendingDownIcon from '@mui/icons-material/TrendingDown';
import { useOptionChainData } from './hooks/useOptionChainData';
import { useMarketData } from '../../hooks/useMarketData';
import { OptionChainData, StrikeData } from '../../types/options';

interface Props {
  symbol: string;
}

export const OptionChainGrid: React.FC<Props> = ({ symbol: initialSymbol }) => {
  const [selectedSymbol, setSelectedSymbol] = useState(initialSymbol);
  const [selectedExpiry, setSelectedExpiry] = useState<string>('');
  const [threshold, setThreshold] = useState('0.05');
  const [appliedFilters, setAppliedFilters] = useState({
    symbol: initialSymbol,
    expiry: '',
    threshold: '0.05'
  });

  // Get expiry dates and option chain data
  const {
    expiryDates,
    loading: expiryLoading,
    error: expiryError,
    fetchExpiryDates,
  } = useOptionChainData({
    symbol: appliedFilters.symbol,
    expiry: appliedFilters.expiry,
    threshold: parseFloat(appliedFilters.threshold)
  });

  // Get market data
  const { optionChainData } = useMarketData(appliedFilters.symbol, appliedFilters.expiry);

  // Fetch expiry dates when symbol changes
  useEffect(() => {
    fetchExpiryDates(selectedSymbol);
  }, [selectedSymbol, fetchExpiryDates]);

  const handleSymbolChange = (event: SelectChangeEvent<string>) => {
    const newSymbol = event.target.value;
    setSelectedSymbol(newSymbol);
    setSelectedExpiry(''); // Reset expiry when symbol changes
  };

  const handleExpiryChange = (event: SelectChangeEvent<string>) => {
    setSelectedExpiry(event.target.value);
  };

  const handleThresholdChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setThreshold(event.target.value);
  };

  const handleApply = useCallback(() => {
    setAppliedFilters({
      symbol: selectedSymbol,
      expiry: selectedExpiry,
      threshold: threshold
    });
  }, [selectedSymbol, selectedExpiry, threshold]);

  const handleRefresh = useCallback(() => {
    handleApply();
  }, [handleApply]);

  // Function to render stock info card
  const renderStockInfo = () => (
    <Card variant="outlined" sx={{ mb: 2 }}>
      <CardContent sx={{ p: 2, '&:last-child': { pb: 2 } }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
          <Typography variant="h6" component="div">
            {optionChainData?.stockInfo.name}
          </Typography>
          <IconButton onClick={handleRefresh} size="small">
            <RefreshIcon />
          </IconButton>
        </Box>
        <Box sx={{ display: 'flex', gap: 3, flexWrap: 'wrap' }}>
          <Chip
            icon={optionChainData?.stockInfo.change >= 0 ? <TrendingUpIcon /> : <TrendingDownIcon />}
            label={`LTP: ${optionChainData?.stockInfo.ltp}`}
            color={optionChainData?.stockInfo.change >= 0 ? 'success' : 'error'}
            variant="outlined"
          />
          <Chip
            label={`Change: ${optionChainData?.stockInfo.change}%`}
            color={optionChainData?.stockInfo.change >= 0 ? 'success' : 'error'}
            variant="outlined"
          />
          <Chip
            label={`Lot Size: ${optionChainData?.stockInfo.lotSize}`}
            variant="outlined"
          />
          <Chip
            label={`Future: ${optionChainData?.stockInfo.future}`}
            variant="outlined"
          />
          <Chip
            label={`VIX: ${optionChainData?.stockInfo.vix}`}
            variant="outlined"
          />
        </Box>
      </CardContent>
    </Card>
  );

  // Function to render a single row of option data
  const renderOptionRow = (strike: StrikeData, type: 'call' | 'put') => {
    const option = type === 'call' ? strike.call : strike.put;
    return (
      <Box 
        key={`${type}-${strike.strikePrice}`}
        sx={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(5, 1fr)',
          gap: 1,
          p: 1,
          '&:nth-of-type(odd)': { bgcolor: 'grey.50' },
          '&:hover': { 
            bgcolor: 'action.hover',
            cursor: 'pointer',
          },
          transition: 'background-color 0.2s',
          borderRadius: 1,
        }}
      >
        <Tooltip title="Last Traded Price" arrow>
          <Typography sx={{ fontWeight: 500 }}>{option.ltp?.toFixed(2) || '-'}</Typography>
        </Tooltip>
        <Typography 
          sx={{ 
            color: option.change >= 0 ? 'success.main' : 'error.main',
            fontWeight: 500,
          }}
        >
          {option.change?.toFixed(2) || '-'}%
        </Typography>
        <Tooltip title="Open Interest" arrow>
          <Typography>{option.oi || '-'}</Typography>
        </Tooltip>
        <Tooltip title="Implied Volatility" arrow>
          <Typography>{option.iv?.toFixed(2) || '-'}</Typography>
        </Tooltip>
        <Tooltip title="Delta" arrow>
          <Typography>{option.delta?.toFixed(3) || '-'}</Typography>
        </Tooltip>
      </Box>
    );
  };

  // Function to render the header for option columns
  const renderColumnHeaders = () => (
    <Box sx={{ 
      display: 'grid', 
      gridTemplateColumns: 'repeat(5, 1fr)',
      gap: 1,
      p: 1,
      bgcolor: 'primary.main',
      color: 'white',
      borderRadius: '4px 4px 0 0',
      fontWeight: 'bold',
    }}>
      <Typography fontWeight="bold">LTP</Typography>
      <Typography fontWeight="bold">Change%</Typography>
      <Typography fontWeight="bold">OI</Typography>
      <Typography fontWeight="bold">IV</Typography>
      <Typography fontWeight="bold">Delta</Typography>
    </Box>
  );

  return (
    <Box sx={{ maxWidth: 1400, mx: 'auto', p: 2 }}>
      {/* Controls */}
      <Card sx={{ mb: 2 }}>
        <CardContent sx={{ p: 2, '&:last-child': { pb: 2 } }}>
          <Box sx={{ 
            display: 'flex', 
            gap: 2, 
            alignItems: 'center',
            flexWrap: 'wrap'
          }}>
            <FormControl sx={{ minWidth: 120 }}>
              <InputLabel>Symbol</InputLabel>
              <Select
                value={selectedSymbol}
                label="Symbol"
                onChange={handleSymbolChange}
                disabled={expiryLoading}
                size="small"
              >
                <MenuItem value="NIFTY">NIFTY</MenuItem>
                <MenuItem value="BANKNIFTY">BANKNIFTY</MenuItem>
              </Select>
            </FormControl>

            <FormControl sx={{ minWidth: 120 }}>
              <InputLabel>Expiry</InputLabel>
              <Select
                value={selectedExpiry}
                label="Expiry"
                onChange={handleExpiryChange}
                disabled={expiryLoading || expiryDates.length === 0}
                size="small"
              >
                {expiryDates.map(date => (
                  <MenuItem key={date} value={date}>{date}</MenuItem>
                ))}
              </Select>
            </FormControl>

            <TextField
              label="Threshold"
              value={threshold}
              onChange={handleThresholdChange}
              sx={{ width: 100 }}
              disabled={expiryLoading}
              type="number"
              inputProps={{ step: 0.01, min: 0 }}
              size="small"
            />

            <Button 
              variant="contained" 
              onClick={handleApply}
              disabled={expiryLoading || !selectedExpiry}
              sx={{ height: 40 }}
            >
              Apply
            </Button>
          </Box>
        </CardContent>
      </Card>

      {/* Error Message */}
      {expiryError && (
        <Typography color="error" sx={{ mt: 2 }}>
          {expiryError}
        </Typography>
      )}

      {/* Loading Indicator */}
      {expiryLoading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <CircularProgress />
        </Box>
      )}

      {/* Stock Information */}
      {optionChainData?.stockInfo && renderStockInfo()}

      {/* Option Chain Grid */}
      {optionChainData?.strikes && (
        <Card sx={{ height: 'calc(100vh - 300px)' }}>
          <CardContent sx={{ p: 2, height: '100%', '&:last-child': { pb: 2 } }}>
            <Box sx={{ display: 'flex', gap: 2, height: '100%' }}>
              {/* Calls Grid */}
              <Box sx={{ flex: 1 }}>
                <Typography variant="h6" align="center" sx={{ mb: 2, color: 'success.main' }}>
                  Calls
                </Typography>
                <Box sx={{ height: 'calc(100% - 40px)', display: 'flex', flexDirection: 'column' }}>
                  {renderColumnHeaders()}
                  <Box sx={{ 
                    flex: 1, 
                    overflow: 'auto',
                    '&::-webkit-scrollbar': {
                      width: '8px',
                    },
                    '&::-webkit-scrollbar-track': {
                      background: '#f1f1f1',
                      borderRadius: '4px',
                    },
                    '&::-webkit-scrollbar-thumb': {
                      background: '#888',
                      borderRadius: '4px',
                    },
                    '&::-webkit-scrollbar-thumb:hover': {
                      background: '#555',
                    },
                  }}>
                    {optionChainData.strikes.map((strike) => renderOptionRow(strike, 'call'))}
                  </Box>
                </Box>
              </Box>

              {/* Strike Prices */}
              <Divider orientation="vertical" flexItem />
              <Box sx={{ width: 120 }}>
                <Typography variant="h6" align="center" sx={{ mb: 2 }}>
                  Strike
                </Typography>
                <Box sx={{ height: 'calc(100% - 40px)', display: 'flex', flexDirection: 'column' }}>
                  <Box sx={{ 
                    p: 1,
                    bgcolor: 'primary.main',
                    color: 'white',
                    textAlign: 'center',
                    borderRadius: '4px 4px 0 0',
                  }}>
                    <Typography fontWeight="bold">Price</Typography>
                  </Box>
                  <Box sx={{ 
                    flex: 1, 
                    overflow: 'auto',
                    '&::-webkit-scrollbar': {
                      width: '8px',
                    },
                    '&::-webkit-scrollbar-track': {
                      background: '#f1f1f1',
                      borderRadius: '4px',
                    },
                    '&::-webkit-scrollbar-thumb': {
                      background: '#888',
                      borderRadius: '4px',
                    },
                    '&::-webkit-scrollbar-thumb:hover': {
                      background: '#555',
                    },
                  }}>
                    {optionChainData.strikes.map((strike) => (
                      <Box 
                        key={`strike-${strike.strikePrice}`}
                        sx={{ 
                          p: 1,
                          textAlign: 'center',
                          fontWeight: 'bold',
                          '&:nth-of-type(odd)': { bgcolor: 'grey.50' },
                          '&:hover': { bgcolor: 'action.hover' },
                          transition: 'background-color 0.2s',
                          borderRadius: 1,
                        }}
                      >
                        <Typography fontWeight="bold">{strike.strikePrice}</Typography>
                      </Box>
                    ))}
                  </Box>
                </Box>
              </Box>
              <Divider orientation="vertical" flexItem />

              {/* Puts Grid */}
              <Box sx={{ flex: 1 }}>
                <Typography variant="h6" align="center" sx={{ mb: 2, color: 'error.main' }}>
                  Puts
                </Typography>
                <Box sx={{ height: 'calc(100% - 40px)', display: 'flex', flexDirection: 'column' }}>
                  {renderColumnHeaders()}
                  <Box sx={{ 
                    flex: 1, 
                    overflow: 'auto',
                    '&::-webkit-scrollbar': {
                      width: '8px',
                    },
                    '&::-webkit-scrollbar-track': {
                      background: '#f1f1f1',
                      borderRadius: '4px',
                    },
                    '&::-webkit-scrollbar-thumb': {
                      background: '#888',
                      borderRadius: '4px',
                    },
                    '&::-webkit-scrollbar-thumb:hover': {
                      background: '#555',
                    },
                  }}>
                    {optionChainData.strikes.map((strike) => renderOptionRow(strike, 'put'))}
                  </Box>
                </Box>
              </Box>
            </Box>
          </CardContent>
        </Card>
      )}
    </Box>
  );
};
