import React, { useState } from 'react';
import { Box, Tabs, Tab, Typography, Container, Alert } from '@mui/material';
import { OptionChainGrid } from './OptionChain/OptionChainGrid';
import { TradeSummary } from './Trade/TradeSummary';
import { VolatilityAnalysis } from './Analysis/VolatilityAnlaysis';
import { StrategyBuilder } from './Strategy/StrategyBuilder';
import { useSelector } from 'react-redux';
import type { RootState } from '../store';

interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`tabpanel-${index}`}
      aria-labelledby={`tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box>
          {children}
        </Box>
      )}
    </div>
  );
}

const MainContent: React.FC = () => {
  const [value, setValue] = useState(0);
  const { isAuthenticated, error } = useSelector((state: RootState) => state.auth);

  const handleChange = (event: React.SyntheticEvent, newValue: number) => {
    setValue(newValue);
  };

  if (!isAuthenticated) {
    return (
      <Container sx={{ mt: 4 }}>
        <Alert severity="info">
          Please set your Kite access token in Settings to start trading.
        </Alert>
      </Container>
    );
  }

  return (
    <Box sx={{ width: '100%' }}>
      <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
        <Tabs value={value} onChange={handleChange} aria-label="trading tabs">
          <Tab label="Option Chain" />
          <Tab label="Trade Summary" />
          <Tab label="Volatility Analysis" />
          <Tab label="Strategy Builder" />
        </Tabs>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mt: 2 }}>
          {error}
        </Alert>
      )}

      <TabPanel value={value} index={0}>
        <OptionChainGrid />
      </TabPanel>
      <TabPanel value={value} index={1}>
        <TradeSummary />
      </TabPanel>
      <TabPanel value={value} index={2}>
        <VolatilityAnalysis />
      </TabPanel>
      <TabPanel value={value} index={3}>
        <StrategyBuilder />
      </TabPanel>
    </Box>
  );
};

export default MainContent;
