import { WSMessage } from '../types/options';

class WebSocketService {
    private ws: WebSocket | null = null;
    private reconnectAttempts = 0;
    private maxReconnectAttempts = 5;
    private reconnectTimeout = 3000; // 3 seconds
    private messageHandlers: ((message: WSMessage) => void)[] = [];
    private subscribedInstruments: Set<number> = new Set();
    private currentSymbol: string | null = null;
    private currentExpiry: string | null = null;

    constructor() {
        this.connect = this.connect.bind(this);
        this.disconnect = this.disconnect.bind(this);
        this.sendMessage = this.sendMessage.bind(this);
        this.addMessageHandler = this.addMessageHandler.bind(this);
        this.removeMessageHandler = this.removeMessageHandler.bind(this);
        this.subscribe = this.subscribe.bind(this);
        this.unsubscribe = this.unsubscribe.bind(this);
    }

    connect(symbol: string, expiry: string) {
        if (this.ws?.readyState === WebSocket.OPEN) {
            this.disconnect();
        }

        const token = process.env.REACT_APP_KITE_API_KEY;
        if (!token) {
            console.error('No API key found in environment variables');
            return;
        }

        this.currentSymbol = symbol;
        this.currentExpiry = expiry;

        const wsUrl = `ws://localhost:8000/ws/options/${symbol}/${expiry}?token=${token}`;
        console.log('Connecting to WebSocket:', wsUrl);

        this.ws = new WebSocket(wsUrl);

        this.ws.onopen = () => {
            console.log('WebSocket connected');
            this.reconnectAttempts = 0;
            // Resubscribe to instruments if any
            if (this.subscribedInstruments.size > 0) {
                this.sendMessage({
                    type: 'subscribe',
                    instruments: Array.from(this.subscribedInstruments)
                });
            }
        };

        this.ws.onmessage = (event) => {
            try {
                const message: WSMessage = JSON.parse(event.data);
                this.messageHandlers.forEach(handler => handler(message));
            } catch (error) {
                console.error('Error parsing WebSocket message:', error);
            }
        };

        this.ws.onerror = (event) => {
            console.error('WebSocket error:', event);
        };

        this.ws.onclose = (event) => {
            console.log('WebSocket disconnected');

            // Attempt to reconnect if not max attempts
            if (this.reconnectAttempts < this.maxReconnectAttempts) {
                console.log(`Reconnecting... Attempt ${this.reconnectAttempts + 1}/${this.maxReconnectAttempts}`);
                setTimeout(() => {
                    this.reconnectAttempts++;
                    if (this.currentSymbol && this.currentExpiry) {
                        this.connect(this.currentSymbol, this.currentExpiry);
                    }
                }, this.reconnectTimeout);
            } else {
                console.error('Max reconnection attempts reached');
            }
        };
    }

    disconnect() {
        if (this.ws) {
            this.ws.close();
            this.ws = null;
            this.subscribedInstruments.clear();
            this.currentSymbol = null;
            this.currentExpiry = null;
        }
    }

    sendMessage(message: any) {
        if (this.ws?.readyState === WebSocket.OPEN) {
            this.ws.send(JSON.stringify(message));
        } else {
            console.error('WebSocket is not connected');
        }
    }

    subscribe(instruments: number[]) {
        instruments.forEach(token => this.subscribedInstruments.add(token));
        this.sendMessage({
            type: 'subscribe',
            instruments
        });
    }

    unsubscribe(instruments: number[]) {
        instruments.forEach(token => this.subscribedInstruments.delete(token));
        this.sendMessage({
            type: 'unsubscribe',
            instruments
        });
    }

    addMessageHandler(handler: (message: WSMessage) => void) {
        this.messageHandlers.push(handler);
    }

    removeMessageHandler(handler: (message: WSMessage) => void) {
        const index = this.messageHandlers.indexOf(handler);
        if (index !== -1) {
            this.messageHandlers.splice(index, 1);
        }
    }
}

export const webSocketService = new WebSocketService();
