import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Button,
  Typography,
  SelectChangeEvent,
} from '@mui/material';
import { optionsApi } from '../../services/api';
import { webSocketService } from '../../services/websocket';
import type { OptionChainData, OptionChainParams } from '../../types/options';

interface Props {
  symbol: string;
}

export const OptionChainGrid: React.FC<Props> = ({ symbol }) => {
  const [selectedSymbol, setSelectedSymbol] = useState(symbol);
  const [selectedExpiry, setSelectedExpiry] = useState<string>('');
  const [expiryDates, setExpiryDates] = useState<string[]>([]);
  const [threshold, setThreshold] = useState('0.05');
  const [optionChainData, setOptionChainData] = useState<OptionChainData | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Fetch expiry dates when symbol changes
  useEffect(() => {
    const fetchExpiryDates = async () => {
      try {
        const response = await optionsApi.getExpiryDates(selectedSymbol);
        setExpiryDates(response.data);
        if (response.data.length > 0) {
          setSelectedExpiry(response.data[0]);
        }
      } catch (error) {
        console.error('Failed to fetch expiry dates:', error);
        setError('Failed to load expiry dates');
      }
    };

    fetchExpiryDates();
  }, [selectedSymbol]);

  // Connect to WebSocket when symbol and expiry are selected
  useEffect(() => {
    if (selectedSymbol && selectedExpiry) {
      webSocketService.connect(selectedSymbol, selectedExpiry);

      const handleMessage = (message: any) => {
        if (message.type === 'OPTION_UPDATE') {
          setOptionChainData(message.data);
        }
      };

      webSocketService.addMessageHandler(handleMessage);

      return () => {
        webSocketService.removeMessageHandler(handleMessage);
        webSocketService.disconnect();
      };
    }
  }, [selectedSymbol, selectedExpiry]);

  const handleSymbolChange = (event: SelectChangeEvent<string>) => {
    setSelectedSymbol(event.target.value);
  };

  const handleExpiryChange = (event: SelectChangeEvent<string>) => {
    setSelectedExpiry(event.target.value);
  };

  const handleThresholdChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setThreshold(event.target.value);
  };

  const handleShowClick = () => {
    if (selectedSymbol && selectedExpiry) {
      webSocketService.disconnect();
      webSocketService.connect(selectedSymbol, selectedExpiry);
    }
  };

  return (
    <Box>
      <Paper elevation={3} sx={{ p: 2, mb: 2 }}>
        <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
          <FormControl sx={{ minWidth: 120 }}>
            <InputLabel>Symbol</InputLabel>
            <Select
              value={selectedSymbol}
              label="Symbol"
              onChange={handleSymbolChange}
            >
              <MenuItem value="NIFTY">NIFTY</MenuItem>
              <MenuItem value="BANKNIFTY">BANKNIFTY</MenuItem>
            </Select>
          </FormControl>

          <FormControl sx={{ minWidth: 120 }}>
            <InputLabel>Expiry</InputLabel>
            <Select
              value={selectedExpiry}
              label="Expiry"
              onChange={handleExpiryChange}
            >
              {expiryDates.map(date => (
                <MenuItem key={date} value={date}>{date}</MenuItem>
              ))}
            </Select>
          </FormControl>

          <TextField
            label="Threshold"
            value={threshold}
            onChange={handleThresholdChange}
            sx={{ width: 100 }}
          />

          <Button
            variant="contained"
            onClick={handleShowClick}
            disabled={!selectedSymbol || !selectedExpiry}
          >
            Show
          </Button>
        </Box>
      </Paper>

      {/* Option chain data will be rendered here */}
      {error && (
        <Typography color="error" sx={{ mt: 2 }}>
          {error}
        </Typography>
      )}
    </Box>
  );
};
