import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { OptionChainData } from '../../types/options';

interface OptionChainState {
  data: OptionChainData | null;
  loading: boolean;
  error: string | null;
}

const initialState: OptionChainState = {
  data: null,
  loading: false,
  error: null,
};

const optionChainSlice = createSlice({
  name: 'optionChain',
  initialState,
  reducers: {
    setOptionChainData: (state, action: PayloadAction<OptionChainData>) => {
      state.data = action.payload;
      state.error = null;
    },
    updateOptionData: (state, action: PayloadAction<Partial<OptionChainData>>) => {
      if (state.data) {
        state.data = {
          ...state.data,
          ...action.payload,
        };
      }
    },
    setLoading: (state, action: PayloadAction<boolean>) => {
      state.loading = action.payload;
    },
    setError: (state, action: PayloadAction<string>) => {
      state.error = action.payload;
      state.loading = false;
    },
    resetOptionChain: (state) => {
      state.data = null;
      state.loading = false;
      state.error = null;
    },
  },
});

export const {
  setOptionChainData,
  updateOptionData,
  setLoading,
  setError,
  resetOptionChain,
} = optionChainSlice.actions;

export default optionChainSlice.reducer;
