import React, { useState, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { 
  Box, 
  TextField, 
  Button, 
  CircularProgress, 
  Snackbar, 
  Alert, 
  Typography,
  Paper
} from '@mui/material';
import type { RootState } from '../../store';
import { setAccessToken, setError as setAuthError } from '../../store/slices/authSlice';
import { marketDataApi, ApiError } from '../../services/api/marketDataApi';

export const AccessTokenInput: React.FC = () => {
  const dispatch = useDispatch();
  const { isAuthenticated, loading: authLoading, error: authError } = useSelector((state: RootState) => state.auth);
  const [token, setToken] = useState('');
  const [loading, setLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorDetails, setErrorDetails] = useState<string | null>(null);

  const validateTokenFormat = (token: string): boolean => {
    // Basic token format validation
    if (token.length < 32) {
      setErrorDetails('Token seems too short. Kite tokens are typically 32 characters or longer.');
      return false;
    }
    if (!/^[A-Za-z0-9]+$/.test(token)) {
      setErrorDetails('Token should only contain letters and numbers.');
      return false;
    }
    return true;
  };

  const validateToken = async (tokenToValidate: string) => {
    setLoading(true);
    setErrorDetails(null);
    console.log('Starting token validation...'); // Debug log

    if (!validateTokenFormat(tokenToValidate)) {
      setLoading(false);
      return;
    }

    try {
      console.log('Making API call to validate token...'); // Debug log
      const response = await marketDataApi.validateToken(tokenToValidate);
      console.log('Validation successful:', response); // Debug log

      localStorage.setItem('kite_access_token', tokenToValidate);
      dispatch(setAccessToken(tokenToValidate));
      setSuccessMessage(`Token validated successfully for user: ${response.user}`);
      setToken('');
      setErrorDetails(null);

    } catch (error) {
      console.error('Token validation failed:', error);

      let errorMessage = 'Token validation failed';
      let details = '';

      if (error instanceof ApiError) {
        errorMessage = error.message;
        details = error.details;
      } else if (error instanceof Error) {
        errorMessage = error.message;
      }

      dispatch(setAuthError(errorMessage));
      setErrorDetails(details);
      dispatch(setAccessToken(null));
      localStorage.removeItem('kite_access_token');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = useCallback(async (e: React.FormEvent) => {
    e.preventDefault();
    if (!token.trim()) {
      dispatch(setAuthError('Please enter an access token'));
      return;
    }
    await validateToken(token.trim());
  }, [token, dispatch]);

  const handleTokenChange = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    const newToken = e.target.value.trim();
    setToken(newToken);
    if (authError) {
      dispatch(setAuthError(null));
    }
    setErrorDetails(null);
  }, [authError, dispatch]);

  const handleTestApi = async () => {
    if (!token.trim()) {
      dispatch(setAuthError('Please enter an access token'));
      return;
    }

    setLoading(true);
    try {
      const result = await marketDataApi.testKiteApi(token.trim());
      console.log('API Test Result:', result);

      if (result.status === 'success') {
        setSuccessMessage('API Test successful! Check console for details.');
      } else {
        dispatch(setAuthError(`API Test failed: ${result.error_message}`));
      }
    } catch (error) {
      console.error('API Test failed:', error);
      dispatch(setAuthError('API Test failed. Check console for details.'));
    } finally {
      setLoading(false);
    }
  };

  const handleDirectTest = async () => {
    if (!token.trim()) {
      dispatch(setAuthError('Please enter an access token'));
      return;
    }

    setLoading(true);
    try {
      const result = await marketDataApi.directTestKiteApi(token.trim());
      console.log('Direct API Test Results:', result);

      // Check REST API call result
      const restApiCall = result.rest_api_call;
      if (restApiCall.status_code === 200) {
        setSuccessMessage('REST API call successful! Check console for details.');
      } else {
        dispatch(setAuthError(`REST API call failed: ${restApiCall.response || restApiCall.error}`));
      }

      // Check KiteConnect call result
      const kiteConnectCall = result.kiteconnect_call;
      if (kiteConnectCall.success) {
        setSuccessMessage((prev) => `${prev}\nKiteConnect call successful!`);
      } else {
        dispatch(setAuthError((prev) => `${prev}\nKiteConnect call failed: ${kiteConnectCall.error}`));
      }

    } catch (error) {
      console.error('Direct test failed:', error);
      dispatch(setAuthError('Direct test failed. Check console for details.'));
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    setSuccessMessage('');
  };

  return (
    <Box component="form" onSubmit={handleSubmit}>
      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <Typography variant="h6" gutterBottom color="primary">
          Token Validation Status
        </Typography>
        <Typography variant="body2" color="text.secondary" paragraph>
          {isAuthenticated 
            ? '✅ Token is valid and active' 
            : '❌ No valid token. Please enter your Kite access token below.'}
        </Typography>

        <TextField
          fullWidth
          label="Access Token"
          value={token}
          onChange={handleTokenChange}
          error={Boolean(authError)}
          helperText={authError || ''}
          disabled={loading || authLoading}
          placeholder="Enter your Kite access token"
          sx={{ mb: 2 }}
        />

        {errorDetails && (
          <Alert severity="warning" sx={{ mb: 2 }}>
            <Typography variant="body2">{errorDetails}</Typography>
          </Alert>
        )}

        <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
          <Button
            type="submit"
            variant="contained"
            disabled={loading || authLoading}
            sx={{ flex: 1, minWidth: '120px' }}
          >
            {loading || authLoading ? (
              <CircularProgress size={24} />
            ) : isAuthenticated ? 'Update Token' : 'Validate Token'}
          </Button>

          <Button
            type="button"
            variant="outlined"
            onClick={handleTestApi}
            disabled={loading || authLoading}
            sx={{ flex: 1, minWidth: '120px' }}
          >
            Test API
          </Button>

          <Button
            type="button"
            variant="outlined"
            onClick={handleDirectTest}
            disabled={loading || authLoading}
            sx={{ flex: 1, minWidth: '120px' }}
            color="secondary"
          >
            Direct Test
          </Button>
        </Box>
      </Paper>

      <Snackbar 
        open={Boolean(successMessage)} 
        autoHideDuration={6000} 
        onClose={handleClose}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert onClose={handleClose} severity="success" variant="filled">
          {successMessage}
        </Alert>
      </Snackbar>
    </Box>
  );
};
