import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import {
    AppBar,
    Box,
    Container,
    Grid,
    Paper,
    Toolbar,
    Typography,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
} from '@mui/material';
import { OptionChainGrid } from './components/OptionChain/OptionChainGrid';
import { SpotChart } from './components/Charts/SpotChart';
import { DepthView } from './components/OrderBook/DepthView';
import { useOptionChainData } from './features/optionChain/hooks/useOptionChainData';

const darkTheme = createTheme({
    palette: {
        mode: 'dark',
    },
});

function App() {
    const [selectedExpiry, setSelectedExpiry] = useState<string>('');
    const symbol = 'NIFTY';

    const { data: optionChainData } = useOptionChainData(symbol, selectedExpiry);

    return (
        <ThemeProvider theme={darkTheme}>
            <CssBaseline />
            <Box sx={{ flexGrow: 1 }}>
                <AppBar position="static">
                    <Toolbar>
                        <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
                            NIFTY 50 Trading System
                        </Typography>
                    </Toolbar>
                </AppBar>
                <Container maxWidth="xl" sx={{ mt: 4, mb: 4 }}>
                    <Grid container spacing={3}>
                        {/* Expiry Selector */}
                        <Grid item xs={12}>
                            <Paper sx={{ p: 2 }}>
                                <FormControl fullWidth>
                                    <InputLabel>Expiry Date</InputLabel>
                                    <Select
                                        value={selectedExpiry}
                                        label="Expiry Date"
                                        onChange={(e) => setSelectedExpiry(e.target.value)}
                                    >
                                        {optionChainData?.expiry_dates.map((date) => (
                                            <MenuItem key={date} value={date}>
                                                {date}
                                            </MenuItem>
                                        ))}
                                    </Select>
                                </FormControl>
                            </Paper>
                        </Grid>

                        {/* Spot Price Chart */}
                        <Grid item xs={12} md={8}>
                            <Paper sx={{ p: 2 }}>
                                {optionChainData && (
                                    <SpotChart
                                        data={[{
                                            instrument_token: 0,
                                            timestamp: new Date().toISOString(),
                                            last_price: optionChainData.spot_price,
                                            volume: 0
                                        }]}
                                        title="NIFTY 50 Spot Price"
                                    />
                                )}
                            </Paper>
                        </Grid>

                        {/* Market Depth */}
                        <Grid item xs={12} md={4}>
                            <Paper sx={{ p: 2 }}>
                                {/* Market depth will be implemented when we have the data */}
                                <Typography variant="h6">Market Depth</Typography>
                            </Paper>
                        </Grid>

                        {/* Option Chain */}
                        <Grid item xs={12}>
                            <Paper sx={{ p: 2 }}>
                                <OptionChainGrid
                                    symbol={symbol}
                                    expiry={selectedExpiry}
                                />
                            </Paper>
                        </Grid>
                    </Grid>
                </Container>
            </Box>
        </ThemeProvider>
    );
}

export default App;
