import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    TextField,
    Button,
    Box,
    Snackbar,
    Alert,
    CircularProgress,
} from '@mui/material';
import type { RootState } from '../../store';
import { setAccessToken, setError } from '../../store/slices/authSlice';

export const AccessTokenInput: React.FC = () => {
    const dispatch = useDispatch();
    const { isAuthenticated, loading, error } = useSelector(
        (state: RootState) => state.auth
    );
    const [token, setToken] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (token) {
            dispatch(setAccessToken(token));
            setSuccessMessage('Access token saved successfully');
            setToken('');
        } else {
            dispatch(setError('Please enter an access token'));
        }
    };

    const handleClose = () => {
        setSuccessMessage('');
    };

    const handleErrorClose = () => {
        dispatch(setError(null));
    };

    if (isAuthenticated) {
        return null;
    }

    return (
        <Box
            component="form"
            onSubmit={handleSubmit}
            sx={{
                display: 'flex',
                flexDirection: 'column',
                gap: 2,
                maxWidth: 400,
                margin: '0 auto',
                padding: 2,
            }}
        >
            <TextField
                label="Access Token"
                value={token}
                onChange={(e) => setToken(e.target.value)}
                fullWidth
                required
                disabled={loading}
            />
            <Button
                type="submit"
                variant="contained"
                color="primary"
                disabled={loading}
                startIcon={loading && <CircularProgress size={20} />}
            >
                {loading ? 'Saving...' : 'Save Access Token'}
            </Button>
            <Snackbar
                open={!!successMessage}
                autoHideDuration={6000}
                onClose={handleClose}
            >
                <Alert onClose={handleClose} severity="success">
                    {successMessage}
                </Alert>
            </Snackbar>
            <Snackbar
                open={!!error}
                autoHideDuration={6000}
                onClose={handleErrorClose}
            >
                <Alert onClose={handleErrorClose} severity="error">
                    {error}
                </Alert>
            </Snackbar>
        </Box>
    );
};
