import { useEffect, useRef } from 'react';
import { useAppDispatch, useAppSelector } from '../store/hooks';
import { setOptionChainData } from '../store/slices/optionChainSlice';
import { MarketData, OptionChainData, WSMessage } from '../types/options';

export const useMarketData = (symbol: string, expiry: string) => {
    const dispatch = useAppDispatch();
    const wsRef = useRef<WebSocket | null>(null);
    const { optionChainData, error, isLoading } = useAppSelector(state => state.optionChain);

    useEffect(() => {
        const token = localStorage.getItem('access_token');
        if (!token || !symbol || !expiry) return;

        const ws = new WebSocket(`ws://localhost:8000/options/${symbol}/${expiry}?token=${token}`);
        wsRef.current = ws;

        ws.onmessage = (event) => {
            const message = JSON.parse(event.data) as WSMessage;

            if (message.type === 'MARKET_DATA' && message.data) {
                const marketData = message.data as MarketData;
                dispatch(setOptionChainData(prevState => {
                    if (!prevState) return null;

                    const updatedStrikes = prevState.strikes.map(strike => {
                        // Update call option
                        if (strike.call?.instrument_token === marketData.instrument_token) {
                            return {
                                ...strike,
                                call: {
                                    ...strike.call,
                                    ltp: marketData.last_price,
                                    change: marketData.change,
                                    volume: marketData.volume,
                                    oi: marketData.oi
                                }
                            };
                        }
                        // Update put option
                        if (strike.put?.instrument_token === marketData.instrument_token) {
                            return {
                                ...strike,
                                put: {
                                    ...strike.put,
                                    ltp: marketData.last_price,
                                    change: marketData.change,
                                    volume: marketData.volume,
                                    oi: marketData.oi
                                }
                            };
                        }
                        return strike;
                    });

                    return {
                        ...prevState,
                        strikes: updatedStrikes
                    };
                }));
            } else if (message.type === 'OPTION_CHAIN' && message.data) {
                dispatch(setOptionChainData(message.data as OptionChainData));
            } else if (message.type === 'ERROR') {
                console.error('WebSocket error:', message.error);
            }
        };

        ws.onerror = (error) => {
            console.error('WebSocket error:', error);
        };

        ws.onclose = () => {
            console.log('WebSocket connection closed');
        };

        // Subscribe to instruments when connection is established
        ws.onopen = () => {
            if (optionChainData) {
                const instruments = optionChainData.strikes.flatMap(strike => {
                    const tokens: number[] = [];
                    if (strike.call?.instrument_token) {
                        tokens.push(strike.call.instrument_token);
                    }
                    if (strike.put?.instrument_token) {
                        tokens.push(strike.put.instrument_token);
                    }
                    return tokens;
                });

                ws.send(JSON.stringify({
                    type: 'subscribe',
                    instruments
                }));
            }
        };

        return () => {
            if (wsRef.current) {
                wsRef.current.close();
                wsRef.current = null;
            }
        };
    }, [symbol, expiry, dispatch, optionChainData]);

    return { optionChainData, error, isLoading };
};
